scss:
@mixin parallelogram (
  $height: 150px,
  $width: 250px,
  $color: $primary-colour,
  $skew: -20deg
) {
  height: $height;
  width: $width;
  background-color: #C651CD;
  transform: skew($skew);
}

css:
.star-five-point { 
  height: 0;
  width: 0;
  margin: 50px 0;
  position: relative;
  display: block;
  color: #C651CD;
  border-right: 100px solid transparent;
  border-left: 100px solid transparent;
  border-bottom: 70px solid #C651CD;
  transform: rotate(35deg);
}

.star-five-point::before { 
  height: 0;
  width: 0;
  border-bottom: 80px solid #C651CD;
  border-left: 30px solid transparent;
  border-right: 30px solid transparent;
  position: absolute;
  top: -45px;
  left: -65px;
  display: block;
  content: "";
  transform: rotate(-35deg);
}

.star-five-point::after {
  height: 0;
  width: 0;
  position: absolute;
  display: block;
  color: #C651CD;
  top: 3px;
  left: -105px;
  border-right: 100px solid transparent;
  border-left: 100px solid transparent;
  border-bottom: 70px solid #C651CD;
  content: "";
  transform: rotate(-70deg);
}

scss:
@mixin starFivePoint (
  $multiplier: 1,
  $color: #C651CD
) {
  height: 0;
  width: 0;
  margin: (50px * $multiplier) 0;
  position: relative;
  display: block;
  color: $color;
  border-right: (100px * $multiplier) solid transparent;
  border-left: (100px * $multiplier) solid transparent;
  border-bottom: (70px * $multiplier) solid $color;
  transform: rotate(35deg);

  &::before {
    height: 0;
    width: 0;
    border-bottom: (80px * $multiplier) solid $color;
    border-left: (30px * $multiplier) solid transparent;
    border-right: (30px * $multiplier) solid transparent;
    position: absolute;
    top: -45px * $multiplier;
    left: -65px * $multiplier;
    display: block;
    content: "";
    transform: rotate(-35deg);
  }

  &::after {
    height: 0;
    width: 0;
    position: absolute;
    display: block;
    color: $color;
    top: 3px * $multiplier;
    left: -105px * $multiplier;
    border-right: (100px * $multiplier) solid transparent;
    border-left: (100px * $multiplier) solid transparent;
    border-bottom: (70px * $multiplier) solid $color;
    content: "";
    transform: rotate(-70deg);
  }
}

css:
.star-six-point {
  height: 0;
  width: 0;
  margin-bottom: 60px;
  position: relative;
  border-right: 100px solid transparent;
  border-left: 100px solid transparent;
  border-bottom: 200px solid #C651CD;
}

.star-six-point::before {
  height: 0;
  width: 0;
  border-right: 100px solid transparent;
  border-left: 100px solid transparent;
  border-top: 200px solid #C651CD;
  position: absolute;
  top: 60px;
  left: -105px;
  content: "";
}

scss:
@mixin starSixPoint (
  $multiplier: 1,
  $color: #C651CD
) {
  height: 0;
  width: 0;
  margin-bottom: 60px * $multiplier;
  position: relative;
  border-right: (100px * $multiplier) solid transparent;
  border-left: (100px * $multiplier) solid transparent;
  border-bottom: (200px * $multiplier) solid $color;

  &::before {
    height: 0;
    width: 0;
    border-right: (100px * $multiplier) solid transparent;
    border-left: (100px * $multiplier) solid transparent;
    border-top: (200px * $multiplier) solid $color;
    position: absolute;
    top: 60px * $multiplier;
    left: -105px * $multiplier;
    content: "";
  }
}

css:
.trapezium {
  height: 0;
  width: 250px;
  position: relative;
  border-right: 50px solid transparent;
  border-left: 50px solid transparent;
  border-bottom: 150px solid #C651CD;
}

scss:
@mixin trapezium (
  $width: 250px,
  $border-right: 50px,
  $border-left: 50px,
  $border-bottom: 150px,
  $color: #C651CD
) {
  height: 0;
  width: $width;
  position: relative;
  border-right: $border-right solid transparent;
  border-left: $border-left solid transparent;
  border-bottom: $border-bottom solid $color;
}

